mapper.select:
select * from ( select rownum as RNUM, A.* from ( select board_seq, board_type, board_title, board_contents, hits, reg_date from tbl_board
order by board_seq desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update tbl_board
set hits=hits+1
where board_seq=#{seq}

update tbl_board
set board_title=#{boardTitle},
	board_contents=#{boardContents}
		
where board_seq=#{boardSeq}
